import React, {useState} from 'react';
import './ExpenseItem.css';
import ExpenseDate from './ExpenseDate';
// import Expenses from './Expenses'

const ExpenseItem = (props) => {
    const [title, setTitle] = useState(props.title);
    const [email, setEmail] = useState()
    let change = 'change email'
   const clickHandler = () => {
       setTitle(<input id="email" placeholder="change email" onChange={(e)=>setEmail(e.target.value)}></input>
       );
       console.log(email);

   };






  return (
    <div className='expense-item'>
      {/* <ExpenseDate date={props.date}>{props.date}</ExpenseDate> */}
      <div className='expense-item__description'>
      <div className='expense-item__price'>{props.amount}
      </div>
      <div className='expense-item__price'>{props.lastName}
      </div>
      <h3 color='red'>{title}</h3>
      <button onClick={clickHandler}>{email? 'change email':'update email'}
      </button>
      <div className='expense-date'>{props.date}</div>
      </div>

    </div>

  );
}

export default ExpenseItem;
